import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Accordion, AccordionDetails, AccordionSummary, TextField, Typography, Button, RadioGroup, FormControlLabel, Radio } from '@material-ui/core';
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';


const useStyles = makeStyles((theme) => ({
  root: {
    width: '50%',
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
}));

const Form = () => {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);

    const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
    };


    return(
        <div className={classes.root}>
            <h1>Form</h1>
            <Accordion  expanded={expanded === 'panel1'} onChange={handleChange('panel1')}>
                <AccordionSummary>
                      <Typography className={classes.heading}>Accordion 1</Typography>
                </AccordionSummary>
                <AccordionDetails>
                    <TextField name="First Name"  variant="outlined" label="First Name"></TextField>
                    <TextField name="Surname"  variant="outlined" label="Surname"></TextField>
                    <TextField name="Email Address"  variant="outlined" label="Email Address"></TextField>
                    <Button size="small" color="primary">Next</Button>
                </AccordionDetails>
            </Accordion>
            <Accordion  expanded={expanded === 'panel2'} onChange={handleChange('panel2')}>
                <AccordionSummary>
                    <Typography className={classes.heading}>Accordion 2</Typography>
                </AccordionSummary>
                  <AccordionDetails>
                    <TextField name="Telephone number"  variant="outlined" label="Telephone number"></TextField>
                   <RadioGroup>
                        <FormControlLabel value="female" control={<Radio />} label="Female" />
                        <FormControlLabel value="male" control={<Radio />} label="Male" />
                        <FormControlLabel value="other" control={<Radio />} label="Other" />
                   </RadioGroup>
                    <KeyboardDatePicker
          disableToolbar
          variant="inline"
          format="MM/dd/yyyy"
          margin="normal"
          id="date-picker-inline"
          label="Date picker inline"
          value={selectedDate}
          onChange={handleDateChange}
          KeyboardButtonProps={{
            'aria-label': 'change date',
          }}
        />
                   <Button size="small" color="primary">Next</Button>
                </AccordionDetails>
            </Accordion>
            <Accordion expanded={expanded === 'panel3'} onChange={handleChange('panel3')}>
                <AccordionSummary>
                    <Typography className={classes.heading}>Accordion 3</Typography>
                </AccordionSummary>
                  <AccordionDetails>
                    <TextField name="Comments"  variant="outlined" label="Comments"  fullWidth multiline rows={6}></TextField>
                    <Button size="small" color="primary">Next</Button>
                </AccordionDetails>
            </Accordion>
        </div>
    );
}

export default Form;


