import 'date-fns';
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { 
    Accordion, AccordionDetails, AccordionSummary, TextField, Typography, 
    Button, RadioGroup, FormControlLabel, Radio, Grid, Paper } from '@material-ui/core';
import DateFnsUtils from '@date-io/date-fns';
import {
  MuiPickersUtilsProvider,
  KeyboardDatePicker,
} from '@material-ui/pickers';



const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
  },
   paper: {
    padding: theme.spacing(2),
  },
  form: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
}));

const Form = () => {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);

    const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
    };


    const [selectedDate, setSelectedDate] = React.useState(new Date('2021-07-19T21:11:54'));
    const handleDateChange = (date) => {
    setSelectedDate(date);
  };

    return(
        <Paper className={classes.root}>
            <h1>Form</h1>
            <Accordion  expanded={expanded === 'panel1'} onChange={handleChange('panel1')}>
                <AccordionSummary>
                      <Typography className={classes.heading}>Step 1: Ypur </Typography>
                </AccordionSummary>
                <AccordionDetails>
                    <form>
                        <TextField name="First Name"  variant="outlined" label="First Name"></TextField>
                        <TextField name="Surname"  variant="outlined" label="Surname"></TextField>
                        <TextField name="Email Address"  variant="outlined" label="Email Address"></TextField>
                        <Button size="small" color="primary">Next</Button>
                    </form>
                </AccordionDetails>
            </Accordion>
            <Accordion  expanded={expanded === 'panel2'} onChange={handleChange('panel2')}>
                <AccordionSummary>
                    <Typography className={classes.heading}>Accordion 2</Typography>
                </AccordionSummary>
                  <AccordionDetails>
                    <form>
                    <TextField name="Telephone number"  variant="outlined" label="Telephone number"></TextField>
                   <RadioGroup>
                        <FormControlLabel value="female" control={<Radio />} label="Female" />
                        <FormControlLabel value="male" control={<Radio />} label="Male" />
                        <FormControlLabel value="other" control={<Radio />} label="Other" />
                   </RadioGroup>
               <MuiPickersUtilsProvider 
                utils={DateFnsUtils}
               >
                <Grid container justifyContent="space-around">
                    <KeyboardDatePicker
                    disableToolbar
                    variant="inline"
                    format="MM/dd/yyyy"
                    margin="normal"
                    id="date-picker-inline"
                    label="Date picker inline"
                    value={selectedDate}
                    onChange={handleDateChange}
                    KeyboardButtonProps={{
                        'aria-label': 'change date',
                    }}
                    />    
                </Grid>
                </MuiPickersUtilsProvider>
                   <Button size="small" color="primary">Next</Button>
                   </form>
                </AccordionDetails>
            </Accordion>
            <Accordion expanded={expanded === 'panel3'} onChange={handleChange('panel3')}>
                <AccordionSummary>
                    <Typography className={classes.heading}>Accordion 3</Typography>
                </AccordionSummary>
                  <AccordionDetails>
                  <form>
                    <TextField name="Comments"  variant="outlined" label="Comments"  fullWidth multiline rows={6}></TextField>
                    <Button size="small" color="primary">Next</Button>
                    </form>
                </AccordionDetails>
            </Accordion>
        </Paper>
    );
}

export default Form;


