import 'date-fns';
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { 
    Accordion, AccordionDetails, AccordionSummary, TextField, Typography, 
    Button, RadioGroup, FormControlLabel, Radio, Grid, Paper, AccordionActions } from '@material-ui/core';
import DateFnsUtils from '@date-io/date-fns';
import {
  MuiPickersUtilsProvider,
  KeyboardDatePicker,
} from '@material-ui/pickers';
import { blueGrey } from '@material-ui/core/colors';



const useStyles = makeStyles((theme) => ({
  root: {
    // width: '100%',
    '& .MuiTextField-root': {
    margin: theme.spacing(1),
     width: '25ch'
    },
  },
  heading: {
    fontSize: theme.typography.pxToRem(15),
    fontWeight: theme.typography.fontWeightRegular,
    
  },
//    paper: {
//     padding: theme.spacing(2),
//     borderRadius: 15,
//   },
  form: {
    // display: 'flex',
    // flexWrap: 'wrap',
    // justifyContent: 'center',

    '& .MuiTextField-root': {
      margin: theme.spacing(1),
    width: 400,
    },
    
    
  },
//   accordionSummary: {
//     backgroundColor: 'grey',
    
//   }

 buttonNext: {
    //marginBottom: 10,
    // '& .MuiTextField-root': {
     margin: 'padding-bottom',
    // },
  },
  butto: {
   alignItems: "flex-end"
  },
//    column: {
//     flexBasis: '73.33%',
//   },
//   comments: {
//       width:800
//   }

}));

const Form = () => {
    const classes = useStyles();
    const [expanded, setExpanded] = React.useState(false);

    const handleChange = (panel) => (event, isExpanded) => {
    setExpanded(isExpanded ? panel : false);
    };


    const [selectedDate, setSelectedDate] = React.useState(new Date('2021-07-19T21:11:54'));
    const handleDateChange = (date) => {
    setSelectedDate(date);
  };

    return(
        <Paper className={classes.root}>
            <Accordion  expanded={expanded === 'panel1'} onChange={handleChange('panel1')}>
                <AccordionSummary className={classes.accordionSummary}>
                    <Typography className={classes.heading}>Step 1: Your details</Typography>
                </AccordionSummary>
                <AccordionDetails>
                    <form className={classes.form}>
                        <TextField name="First Name"  variant="outlined" label="First Name"></TextField>
                        <TextField name="Surname"  variant="outlined" label="Surname"></TextField>
                        <TextField name="Email Address"  variant="outlined" label="Email Address"></TextField>
                        <Button className={classes.buttonNext} variant="contained" color="primary" size="small">Next</Button>
                    </form>
                     {/* <Button className={classes.buttonNext} variant="contained" color="primary" size="small">Next2</Button> */}
                </AccordionDetails>
            </Accordion>
            <Accordion  expanded={expanded === 'panel2'} onChange={handleChange('panel2')}>
                <AccordionSummary className={classes.accordionSummary}>
                    <Typography className={classes.heading}>Step 2: More comments</Typography>
                </AccordionSummary>
                  <AccordionDetails>
                    <form  className={classes.form}><div></div>
                    <div style={{}}>
                    <TextField name="Telephone number"  variant="outlined" label="Telephone number"></TextField>
                     <MuiPickersUtilsProvider 
                utils={DateFnsUtils}
               >
                <Grid container justifyContent="space-around">
                    <KeyboardDatePicker
                    disableToolbar
                    variant="inline"
                    format="MM/dd/yyyy"
                    margin="normal"
                    id="date-picker-inline"
                    label="Date picker inline"
                    value={selectedDate}
                    onChange={handleDateChange}
                    KeyboardButtonProps={{
                        'aria-label': 'change date',
                    }}
                    />    
                </Grid>
                </MuiPickersUtilsProvider>
                   </div>
                   <RadioGroup>
                        <FormControlLabel value="female" control={<Radio />} label="Female" />
                        <FormControlLabel value="male" control={<Radio />} label="Male" />
                        <FormControlLabel value="other" control={<Radio />} label="Other" />
                   </RadioGroup>
             
                   <Button className={classes.buttonNext} size="small" variant="contained" color="primary">Next</Button>
                   </form>
                </AccordionDetails>
            </Accordion>
            <Accordion expanded={expanded === 'panel3'} onChange={handleChange('panel3')}>
                <AccordionSummary className={classes.accordionSummary}>
                    <Typography className={classes.heading}>Step 3: Final comments</Typography>
                </AccordionSummary>
                  <AccordionDetails>
                  <div className={classes.comments}>
                  <form  className={classes.form}>
                    <TextField 
                        name="Comments"  
                        variant="outlined" 
                        label="Comments"  
                        fullWidth
                        multiline rows={6} 
                    />
                    </form>
                    </div>
                    <div className={classes.buttonComments}>
                        <Button className={classes.buttonNext} size="small" variant="contained" color="primary">Next</Button>
                    </div>
                </AccordionDetails>
            </Accordion>
        </Paper>
    );
}

export default Form;
